/* Generated by lx */

#ifndef LX_H
#define LX_H

enum lx_token {
	TOK_CLOSECOUNT,
	TOK_OPENCOUNT,
	TOK_COUNT,
	TOK_SEP,
	TOK_CLOSEGROUP,
	TOK_OPENGROUP,
	TOK_RANGE,
	TOK_INVERT,
	TOK_CHAR,
	TOK_HEX,
	TOK_OCT,
	TOK_ESC,
	TOK_ALT,
	TOK_DOT,
	TOK_PLUS,
	TOK_STAR,
	TOK_QMARK,
	TOK_END,
	TOK_START,
	TOK_CLOSESUB,
	TOK_OPENSUB,
	TOK_EOF,
	TOK_ERROR,
	TOK_UNKNOWN
};

struct lx_pos {
	unsigned byte;
	unsigned line;
	unsigned col;
};

struct lx {
	int (*lgetc)(struct lx *lx);
	void *opaque;

	int c; /* lx_simple_ungetc buffer */

	struct lx_pos start;
	struct lx_pos end;

	void *buf;
	int  (*push) (struct lx *lx, char c);
	void (*pop)  (struct lx *lx);
	int  (*clear)(struct lx *lx);
	void (*free) (struct lx *lx);

	enum lx_token (*z)(struct lx *lx);
};

/*
 * The initial buffer size; this ought to be over the typical token length,
 * so as to avoid a run-up of lots of resizing.
 */
#ifndef LX_DYN_LOW
#define LX_DYN_LOW 1 << 10
#endif

/*
 * High watermark; if the buffer grows over this, it will resize back down
 * by LX_DYN_FACTOR when no longer in use.
 */
#ifndef LX_DYN_HIGH
#define LX_DYN_HIGH 1 << 13
#endif

/*
 * Andrew Koenig said the growth factor should be less than phi, (1 + sqrt(5)) / 2
 * P.J. Plauger said 1.5 works well in practice. (Perhaps because of internal
 * bookkeeping data stored by the allocator.)
 *
 * Non-integer factors here add the constraint that LX_DYN_LOW > 1 because
 * because conversion to size_t truncates, and e.g. 1 * 1.5 == 1 is no good
 * as the requirement is to *increase* a buffer.
 */
#ifndef LX_DYN_FACTOR
#define LX_DYN_FACTOR 2
#endif

/* dynamic token buffer */
struct lx_dynbuf {
	char *p;
	size_t len;
	char *a;
};

/* fixed-size token buffer */
struct lx_fixedbuf {
	char *p;
	size_t len;
#ifdef LX_FIXED_SIZE
	char a[LX_FIXED_SIZE];
#else
	char *a; /* could be flexible member */
#endif
};

/* opaque for lx_simple_agetc */
struct lx_arr {
	char *p;
	size_t len;
};

/* opaque for lx_simple_fdgetc */
struct lx_fd {
	char *p;
	size_t len;

	int fd;
	size_t bufsz; /* number of bytes allocated after this struct */
};

const char *lx_simple_name(enum lx_token t);
const char *lx_simple_example(enum lx_token (*z)(struct lx *), enum lx_token t);

void lx_simple_init(struct lx *lx);
enum lx_token lx_simple_next(struct lx *lx);

int lx_simple_fgetc(struct lx *lx);
int lx_simple_sgetc(struct lx *lx);
int lx_simple_agetc(struct lx *lx);
int lx_simple_dgetc(struct lx *lx);

int  lx_simple_dynpush(struct lx *lx, char c);
void lx_simple_dynpop(struct lx *lx);
int  lx_simple_dynclear(struct lx *lx);
void lx_simple_dynfree(struct lx *lx);

int  lx_simple_fixedpush(struct lx *lx, char c);
void lx_simple_fixedpop(struct lx *lx);
int  lx_simple_fixedclear(struct lx *lx);

#endif

